    jdbcClient.getConnection(sqlConnection -&gt; {
        if (sqlConnection.succeeded()) {
            DTOQueryInfo dtoQueryInfo = queryHelper(sqlConnection.result(), id, type);
            // Execute query 
            sqlConnection.result().queryWithParams(dtoQueryInfo.getSqlString(), dtoQueryInfo.getParams(), fetchSequenceResult -&gt; {
