      int i=n-1;
      int m=0;
      while(i&gt;=0){
        if(nums[i]==0){
          nums[i]=m;
        }else if(nums[i]%2==1){
          m=Math.max(m,nums[i]);
        }
        i--;
      }
      return nums;
    }

