public class GroupChoose extends AppCompatActivity {
	
	MaterialButton btF, btS, btT;
    MaterialCardView first, second, third;
    
    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
		setContentView(R.layout.choose_group);
		
		Toolbar toolbar=(Toolbar)findViewById(R.id.toolbar);
		setSupportActionBar(toolbar);

		getSupportActionBar().hide();
		
		btF = findViewById(R.id.btFirstG);
		btS = findViewById(R.id.btSecondG);
		btT = findViewById(R.id.btThirdG);
        first = findViewById(R.id.firstGroup);
        second = findViewById(R.id.secondGroup);
        third = findViewById(R.id.thirdGroup);
		
		btF.setOnClickListener(new OnClickListener(){

				@Override
				public void onClick(View p1) {
					LaunchNext(0);
				}
			});

		btS.setOnClickListener(new OnClickListener(){

				@Override
				public void onClick(View p1) {
					LaunchNext(1);
				}
			});

		btT.setOnClickListener(new OnClickListener(){

				@Override
				public void onClick(View p1) {
					startActivity(new Intent(GroupChoose.this, CustomObjects.class));
				}
			});

    }

	@Override
	public void onBackPressed() {

		finishAffinity();

	}

	void LaunchNext(int groupNum)
	{
		Intent i = new Intent(this, PlayersAdd.class);
		i.putExtra(&quot;chosenOne&quot;, groupNum);

		startActivity(i);
	}

}
