        int rootY = find(y);

        if (rootX != rootY) {
            if (rank[rootX] &lt; rank[rootY]) {
                parent[rootX] = rootY;
            } else if (rank[rootX] &gt; rank[rootY]) {
                parent[rootY] = rootX;
            } else {
                parent[rootY] = rootX;
                rank[rootX]++;
            }
        }
    }
