```java
package FINRA.Apps.eDiscovery.config;

import org.springframework.beans.factory.annotation.Value;
import org.springframework.context.annotation.Bean;
import org.springframework.context.annotation.Configuration;
import org.springframework.context.annotation.Primary;
import org.springframework.data.jpa.repository.config.EnableJpaRepositories;
import org.springframework.instrument.classloading.InstrumentationLoadTimeWeaver;
import org.springframework.jdbc.datasource.DriverManagerDataSource;
import org.springframework.orm.jpa.JpaTransactionManager;
import org.springframework.orm.jpa.LocalContainerEntityManagerFactoryBean;
import org.springframework.orm.jpa.vendor.Database;
import org.springframework.orm.jpa.vendor.HibernateJpaVendorAdapter;
import org.springframework.transaction.PlatformTransactionManager;
import org.springframework.transaction.annotation.EnableTransactionManagement;

import jakarta.persistence.EntityManagerFactory;
import javax.sql.DataSource;
import java.util.Properties;

@Configuration
@EnableJpaRepositories(basePackages = &quot;FINRA.Apps.eDiscovery.jpa.repository&quot;,
entityManagerFactoryRef = &quot;entityManagerFactory&quot;,
transactionManagerRef = &quot;transactionManager&quot;)
@EnableTransactionManagement
public class JpaConfig {

    @Value(&quot;${eDiscovery.postgresql.connectionString}&quot;)
    private String url;
    @Value(&quot;${eDiscovery.postgresql.username}&quot;)
    private String username;
    @Value(&quot;${eDiscovery.postgresql.password}&quot;)
    private String password;

    @Primary
    @Bean
    public LocalContainerEntityManagerFactoryBean entityManagerFactory() {
        LocalContainerEntityManagerFactoryBean factory = new LocalContainerEntityManagerFactoryBean();

        HibernateJpaVendorAdapter vendorAdapter = new HibernateJpaVendorAdapter();
        vendorAdapter.setShowSql(Boolean.FALSE);
        vendorAdapter.setDatabase(Database.POSTGRESQL);

        factory.setDataSource(dataSource());
        factory.setJpaVendorAdapter(vendorAdapter);
        factory.setPackagesToScan(&quot;FINRA.Apps.eDiscovery.jpa.model&quot;);

        final Properties jpaProperties = new Properties();
        setupHibernateProperties(jpaProperties);

        factory.setJpaProperties(jpaProperties);

        factory.afterPropertiesSet();
        factory.setLoadTimeWeaver(new InstrumentationLoadTimeWeaver());
        return factory;
    }

    private static void setupHibernateProperties(Properties jpaProperties) {
        jpaProperties.put(&quot;hibernate.dialect&quot;,
                          &quot;org.hibernate.dialect.PostgreSQLDialect&quot;);
        jpaProperties.put(&quot;hibernate.format_sql&quot;,
                          &quot;true&quot;);
    }

    @Primary
    @Bean
    public DataSource dataSource() {
        DriverManagerDataSource dataSource = new DriverManagerDataSource(url,username,password);
        System.out.println(&quot;#testing&quot; + url + username + password);
        dataSource.setDriverClassName(&quot;org.postgresql.Driver&quot;);
        return dataSource;
    }

    @Primary
    @Bean
    public PlatformTransactionManager transactionManager() {
        EntityManagerFactory factory = entityManagerFactory().getObject();
        return new JpaTransactionManager(factory);
    }

}
