        public static &lt;T extends A&gt; T create(Class&lt;T&gt; type) {
            try {
                T obj = type.newInstance();
                obj.init();
                return obj;
            } catch (ReflectiveOperationException ex) {
                System.err.println(&quot;No default constructor available.&quot;);
                assert false;
                return null;
            }
        }
    }

