    public class GsonDateFormatAdapter implements JsonSerializer&lt;Date&gt;, JsonDeserializer&lt;Date&gt; {
    
    	private final DateFormat dateFormat;
    
    	public GsonDateFormatAdapter(DateFormat dateFormat) {
