		int n1, n2;
		int i, j;

		n1 = q - p + 1;
		n2 = r - q;

		int[] L = new int[n1];
		int[] R = new int[n2];

		for (i = 0; i &lt; n1; i++)
			L[i] = A[p + i];

		for (i = 0; i &lt; n2; i++)
			R[i] = A[q + i + 1];

