	            Long idProperty = environment.getProperty(indexedPropertyKey + &quot;.id&quot;, Long.class);
	            if (idProperty == null) {
	                // No more properties found
	                break;
	            }

	            String nameProperty = environment.getProperty(indexedPropertyKey + &quot;.name&quot;);
	            configs.add(new Category(idProperty, nameProperty));

	            index++;
	        }
