    var size = 20;
    var levels = 5;
    var t = new Turtle();
    t.penup();

    for (var i = 0; i &lt; levels; i++) {
      var boxes = i * 2 + 1;
      var offset = (levels - i - 1) * size;
      t.forward(offset);
      t.pendown();

      for (var j = 0; j &lt; boxes; j++) {
        for (var k = 0; k &lt; 4; k++) {
          t.forward(size);
          t.right(90);
        }

        t.forward(size);
      }

      t.penup();
      t.right(90);
      t.forward(size);
      t.right(90);
      t.forward(offset + size * boxes);
      t.right(180);
    }








