public class CompletableFutureHelper {

    public static &lt;T&gt; CompletableFuture&lt;T&gt; supplyAsync(Callable&lt;T&gt; callable) {
        return supplyAsync(callable, ForkJoinPool.commonPool());
    }

    public static &lt;T&gt; CompletableFuture&lt;T&gt; supplyAsync(Callable&lt;T&gt; callable, Executor executor) {
        final CompletableFuture&lt;T&gt; completableFuture = new CompletableFuture&lt;&gt;();
        executor.execute(() -&gt; {
            try {
                completableFuture.complete(callable.call());
            } catch (Throwable e) {
                completableFuture.completeExceptionally(e);
            }
        });
        return completableFuture;
    }

    public static CompletableFuture&lt;?&gt; runAsync(RunnableExceptionable runnableException) {
        return runAsync(runnableException, ForkJoinPool.commonPool());
    }

    public static CompletableFuture&lt;?&gt; runAsync(RunnableExceptionable runnableException, Executor executor) {
        return supplyAsync(() -&gt; {
            runnableException.run();
            return null;
        }, executor);
    }

    public interface RunnableExceptionable {
        void run() throws Exception;
    }
}
