fun EditText.placeCursorToEnd() {
    this.setOnFocusChangeListener { v, hasFocus -&gt;
        if (hasFocus) {
            v.post { this.setSelection(text.length) }
        }
    }
    this.setOnClickListener {
        it.post { this.setSelection(text.length) }
    }
}
