public static int getLargestPrime(int number) {
    // no primes less than 2 exist.
	if (number &lt; 2) {
		return -1;
	}
	while (number % 2 == 0) {
		number /= 2;
	}
	
	int lastDivisor = 2;
	for (int d = 3; d &lt;= Math.sqrt(number); d += 2) {
		lastDivisor = d;
		while (number % d == 0) {
			number /= d;
		}
	}
	
	return number == 1 ? lastDivisor : number;
}

