                if ((itemToMerge instanceof JSONObject) &amp;&amp; (mergedJson.opt(key) instanceof JSONObject)) {
                    itemToMerge = merge(mergedJson.getJSONObject(key), (JSONObject) itemToMerge);
                }
                mergedJson.putOpt(key, itemToMerge);
            }
        }

        return mergedJson;
    }

