	public void bubbleSort() {
		for (int i = 0; i &lt; number; i++) {
			for (int j = i + 1; j &lt; number; j++) {
				if (list.get(i).compareTo(list.get(j)) &gt; 0) {
					swap(i, j);
				}
			}
		}
	}

