public class SafePoint {

    public static AtomicInteger num = new AtomicInteger(0);

    public static void main(String[] args) throws InterruptedException {
        Runnable runnable=()-&gt;{
            for (long i = 0; i &lt; 1000000000; i++) {
                num.getAndAdd(1);
