    			logger.error(e.toString());
    		} finally {
    			try {
    				if (statement != null) {
    					statement.close();
    					logger.debug(&quot;Closed Statement.&quot;);
    				}
    				if (connection != null) {
    					logger.debug(&quot;Closed Connection.&quot;);
