package examples.stackoverflow.q73637167;

import com.sun.xml.internal.messaging.saaj.packaging.mime.util.BASE64DecoderStream;

import java.io.ByteArrayInputStream;
import java.io.IOException;
import java.io.InputStream;
import java.nio.charset.Charset;
import java.nio.charset.StandardCharsets;

public class Q73637167 {
	private static String decodeMessage(InputStream message, Charset charset) throws IOException {
		String result = &quot;&quot;;
		if(message instanceof ByteArrayInputStream) {
			ByteArrayInputStream bis = (ByteArrayInputStream) message;
			byte[] data = new byte[bis.available()];
			bis.read(data);
			result = new String(data, charset);
		} else if(message instanceof BASE64DecoderStream) {
			BASE64DecoderStream decodeStream = (BASE64DecoderStream) message;

			// Read all the bytes from the array in an ever growing buffer.
			// Didn&#39;t use any utils for minimum dependencies. Don&#39;t to this at home!
			byte[] data = new byte[1024];
			int count = decodeStream.read(data);
			int startPos = 0;
			while(count == 1024) {
				byte[] addBuffer = new byte[data.length + 1024];
				System.arraycopy(data, 0, addBuffer, 0, data.length);
				startPos = data.length;
				data = addBuffer;
				count = decodeStream.read(data, startPos, 1024);
			}
			int maxlen = data.length - 1024 + count;

			// convert already decoded data in byte array to String.
			result = new String(data, 0, maxlen, charset);
		}
		return result;
	}

	public static void main(String[] args) throws IOException {
		// case 1: plain text
		ByteArrayInputStream byis = new ByteArrayInputStream(
				&quot;Test mit &#220;ml&#228;uten. &#246;&#246;&#246;&#223;&#223;&#223;&quot;.getBytes(StandardCharsets.UTF_8));
