  public static boolean isSeedable(RandomGeneratorFactory&lt;RandomGenerator&gt; factory) {
    final int ROUNDS = 3, NUMBERS_PER_ROUND = 100, SEED = 123;
    List&lt;Integer&gt;[] randomNumbers = new List[ROUNDS];
