               Tuples.of(b, a.getT1() + b);
           })
           .skip(1)
           .materialize() 
           .handle((s, t) -&gt; {
              if (t.getType() == SignalType.ON_NEXT) {
                  last = t.get();
                  s.next(t.get().getT1());
              } else if (t.getType() == SignalType.ON_COMPLETE) {
