	// indices p and r can start from 0
	public static void merge_sort(int[] A, int p, int r) {
		int q;

		if (p &lt; r) {
			q = (int) Math.floor((p + r) / 2);
			merge_sort(A, p, q);
			merge_sort(A, q + 1, r);
			merge(A, p, q, r);
