	public static void merge(int[] A, int p, int q, int r) {
		int n1, n2;
		int i, j;

		n1 = q - p + 1;
		n2 = r - q;

		int[] L = new int[n1];
		int[] R = new int[n2];

		for (i = 0; i &lt; n1; i++)
			L[i] = A[p + i];

		for (i = 0; i &lt; n2; i++)
			R[i] = A[q + i + 1];

		i = 0;
		j = 0;

		for (int k = p; k &lt;= r; k++) {
			if (i &gt;= n1) // the left array finished, copy from right array
			{
				A[k] = R[j];
				j++;
				continue;
			}

			if (j &gt;= n2) // the right array finished, copy from left array
			{
				A[k] = L[i];
				i++;
				continue;
			}

			if (L[i] &lt;= R[j]) {
				A[k] = L[i];
				i++;
			} else {
				A[k] = R[j];
				j++;
			}
		}
	}

	// insertion sort algorithm
	public static void insertion_sort_2(int[] A, int start_index, int end_index) {
		int key;
		int i;

		for (int j = start_index + 1; j &lt; end_index; j++) {
			key = A[j];

			// insert A[j] into the sorted sequence A[starting_index..j-1]
			i = j - 1;

			while ((i &gt;= 0) &amp;&amp; (A[i] &gt; key)) {
				A[i + 1] = A[i];
				i = i - 1;
			}

			A[i + 1] = key;
		}
	}

	// insertion sort algorithm
	public static void insertion_sort(int[] A) {
		int key;
		int i;

		for (int j = 1; j &lt; A.length; j++) {
			key = A[j];

			// insert A[j] into the sorted sequence A[1..j-1]
			i = j - 1;

			while ((i &gt;= 0) &amp;&amp; (A[i] &gt; key)) {
				A[i + 1] = A[i];
				i = i - 1;
			}

			A[i + 1] = key;
		}
	}

