                path.add(0, value);
                return Optional.of(new Solution(value + left.sum, path));
            }
        } else {
            if (!right.isPresent()) {
                List&lt;Integer&gt; path = right.path;
                path.add(0, value);
                return Optional.of(new Solution(value + right.sum, path));
            } else {
                if (left.sum &gt; right.sum) {
                    List&lt;Integer&gt; path = left.path;
                    path.add(0, value);
