      String UTC_TIMEZONE_NAME = &quot;UTC&quot;;
    
      static SimpleDateFormat provideDateFormat() {
        SimpleDateFormat simpleDateFormat = new SimpleDateFormat(ISO_DATE_FORMAT_ZERO_OFFSET);
