            System.out.println(&quot;blob : &quot;+blob);
            
            faceNet.setInput(blob);
            System.out.println(&quot;FaceNet : &quot;+faceNet);
            
            Mat detections = faceNet.forward();
            System.out.println(&quot;detections : &quot;+detections);
            
            for (int i = 0; i &lt; detections.size().height; i++) {
