                &lt;url-pattern&gt;/app/*&lt;/url-pattern&gt;
            &lt;/web-resource-collection&gt;
            &lt;auth-constraint&gt;
                &lt;role-name&gt;ADMIN&lt;/role-name&gt;
                &lt;role-name&gt;USER&lt;/role-name&gt;
            &lt;/auth-constraint&gt;
        &lt;/security-constraint&gt;
    
        &lt;security-role&gt;
            &lt;role-name&gt;USER&lt;/role-name&gt;
        &lt;/security-role&gt;
        &lt;security-role&gt;
            &lt;role-name&gt;ADMIN&lt;/role-name&gt;
        &lt;/security-role&gt;


