        ArrayList&lt;Integer&gt; curr = new ArrayList&lt;Integer&gt;();
        ArrayList&lt;ArrayList&lt;Integer&gt;&gt; res = new ArrayList&lt;ArrayList&lt;Integer&gt;&gt;();
        subsetsRec(A, curr, 0, res);
        return res;
    }
}
