public class ConnectedGroups {

    public static void main(String[] args) {
        List&lt;String&gt; adj0 = Arrays.asList(&quot;110&quot;, &quot;110&quot;, &quot;001&quot;);
        List&lt;String&gt; adj1 = Arrays.asList(&quot;10000&quot;,&quot;01000&quot;,&quot;00100&quot;,&quot;00010&quot;,&quot;00001&quot;);
