    import java.io.IOException;  
    import java.util.List;  
    import org.apache.hadoop.fs.BlockLocation;  
    import org.apache.hadoop.fs.FSDataInputStream;  
    import org.apache.hadoop.fs.FileStatus;  
    import org.apache.hadoop.fs.FileSystem;  
    import org.apache.hadoop.fs.Path;  
    import org.apache.hadoop.io.DataOutputBuffer;  
    import org.apache.hadoop.io.LongWritable;  
    import org.apache.hadoop.io.Text;  
    import org.apache.hadoop.mapreduce.InputSplit;  
    import org.apache.hadoop.mapreduce.JobContext;  
    import org.apache.hadoop.mapreduce.RecordReader;  
    import org.apache.hadoop.mapreduce.TaskAttemptContext;  
    import org.apache.hadoop.mapreduce.lib.input.FileSplit;  
    import org.apache.hadoop.mapreduce.lib.input.TextInputFormat;

    public class XmlInputFormat extends TextInputFormat {

	    public static final String START_TAG_KEY = &quot;&lt; student&gt;&quot;;
	    public static final String END_TAG_KEY = &quot;&lt;/student&gt;&quot;;
	
	    @Override
	    public RecordReader&lt;LongWritable, Text&gt; createRecordReader(
		   InputSplit split, TaskAttemptContext context) {
		   return new XmlRecordReader();
	    }
	
	    public static class XmlRecordReader extends 
        RecordReader&lt;LongWritable, Text&gt; {
		    private byte[] startTag;
		    private byte[] endTag;
		    private long start;
		    private long end;
		    private FSDataInputStream fsin;
		    private DataOutputBuffer buffer = new DataOutputBuffer();
		    private LongWritable key = new LongWritable();
		    private Text value = new Text();
		
		    @Override
		    public void initialize(InputSplit is, TaskAttemptContext tac)
		    throws IOException, InterruptedException {
			    FileSplit fileSplit = (FileSplit) is;
			    String START_TAG_KEY = &quot;&lt;employee&gt;&quot;;
			    String END_TAG_KEY = &quot;&lt;/employee&gt;&quot;;
			    startTag = START_TAG_KEY.getBytes(&quot;utf-8&quot;);
			    endTag = END_TAG_KEY.getBytes(&quot;utf-8&quot;);
			
			    start = fileSplit.getStart();
			    end = start + fileSplit.getLength();
			    Path file = fileSplit.getPath();
			
			    FileSystem fs =file.getFileSystem(tac.getConfiguration());
			    fsin = fs.open(fileSplit.getPath());
			    fsin.seek(start);
		
		    }
		
		    @Override
		    public boolean nextKeyValue() throws     
               IOException,InterruptedException {
			    if (fsin.getPos() &lt; end) {
				    if (readUntilMatch(startTag, false)) {
					    try {
						    buffer.write(startTag);
						    if (readUntilMatch(endTag, true)) {
						
							    value.set(buffer.getData(), 0,     
                                     buffer.getLength());
							    key.set(fsin.getPos());
							    return true;
						    }
					    } finally {
						    buffer.reset();
					    }
				    }
			    }
			    return false;
		    }
		
		    @Override
		    public LongWritable getCurrentKey() throws IOException,
		    InterruptedException {
			    return key;
		    }
		
		    @Override
		    public Text getCurrentValue() throws IOException,       
              InterruptedException {
			    return value;
		
		    }
		
		    @Override
		    public float getProgress() throws IOException, 
              InterruptedException {
			    return (fsin.getPos() - start) / (float) (end - start);
		    }
		
		    @Override
		    public void close() throws IOException {
			    fsin.close();
		    }
		
		    private boolean readUntilMatch(byte[] match, boolean 
                withinBlock)throws IOException {
			    int i = 0;
			    while (true) {
				    int b = fsin.read();
				
				    if (b == -1)
					    return false;
				
				    if (withinBlock)
					    buffer.write(b);
				
				    if (b == match[i]) {
					    i++;
					    if (i &gt;= match.length)
					    return true;
				    } else
					    i = 0;
				
				    if (!withinBlock &amp;&amp; i == 0 &amp;&amp; fsin.getPos() &gt;= end)
					    return false;
			    }
		    }
	
	    }

    }

Java Strings have a `length()` method you can use to check the number of characters.

For the first character check you can use the `charAt(index)` method also available in String.

You can combine this with the `Character.isDigit(char)` method to check for the requirement.


I guess a solution could look something like this


    public static List&lt;String&gt; f(List&lt;Integer&gt; integerList, List&lt;String&gt; stringList) {
        return integerList.stream().map(
            integer -&gt; stringList.stream()
                    .filter(s -&gt; s.length() == integer)
                    .filter(s -&gt; Character.isDigit(s.charAt(0)))
                    .findFirst().orElse(&quot;Not found&quot;)
