		Set&lt;Integer&gt; validSet = new TreeSet&lt;Integer&gt;();

		for (int i = 0; i &lt; A.length; i++) {
			actualSet.add(A[i]);
			validSet.add(i + 1);
		}

		for (int value : validSet) {
			if (!actualSet.contains(value)) {
				return value;
			}
		}

		if (actualSet.size() == validSet.size()) {
			return validSet.size() + 1; //e.g. {1, 2, 3} should return 4
		}

		return 1; // if A array has negative values or just a single positive value
	}

