          // found faster. This is called lazy deletion/relocation.
          if (j != -1) {
            // Swap key-values pairs at indexes i and j.
            keys[j] = keys[i];
            values[j] = values[i];
            keys[i] = TOMBSTONE;
            values[i] = null;
            return values[j];
          } else {
