    public class FindRanges {
    
        public static void main(String[] args) {
            char last0 = &#39;\0&#39;;
            char last1 = &#39;\0&#39;;
            for (int x = 0x1F300; x &lt;= 0x1F5FF; ++x) {
                char[] chars = new StringBuilder().appendCodePoint(x).toString().toCharArray();
