      String base64WithoutPrefix = base64Chunk.replaceFirst(&quot;data:application/pdf;base64,&quot;, &quot;&quot;);

      if (base64WithoutPrefix.equals(&quot;LAST_CHUNK_SIGNAL&quot;)) {
