class URLParser {
    private final String query;
    
    public URLParser(String query) {
        this.query = query;
    }
    
    public String get(String name) {
        String regex = &quot;(?:^|\\?|&amp;)&quot; + name + &quot;=(.*?)(?:&amp;|$)&quot;;
        Pattern pattern = Pattern.compile(regex);
