        System.out.println(&quot;EndX: &quot; + endX + &quot; EndY: &quot; + endY);
        System.out.println(&quot;Player: &quot; + player);

        int xDiff = Math.abs(endX - startX);
        int yDiff = Math.abs(endY - startY);

        // Check for a regular move (one square diagonal)
        if (xDiff == 1 &amp;&amp; yDiff == 1) {
            if (board[startY][startX] == player &amp;&amp; board[endY][endX] == &#39;-&#39;) {
                System.out.println(&quot;Regular move.&quot;);
                return true;
