            }
        } else {
            System.out.println(&quot;Regular move failed due to xDiff or yDiff.&quot;);
        }

        // Check for a capture move (two squares diagonal with an opponent&#39;s piece in between)
        if (xDiff == 2 &amp;&amp; yDiff == 2) {
            int midX = (startX + endX) / 2;
            int midY = (startY + endY) / 2;

            // Debugging: Print intermediate values
