    @Override
    public RepositorySystemSession decorate(MavenProject project, RepositorySystemSession session) {
        String enabled = session.getUserProperties().get(&quot;fakerepo&quot;);
        if (!&quot;true&quot;.equalsIgnoreCase(enabled)) {
