	public void executeCommands() throws IOException {

		File tempScript = createTempScript();

		try {
			ProcessBuilder pb = new ProcessBuilder(&quot;bash&quot;, tempScript.toString());
			pb.inheritIO();
			Process process = pb.start();
			process.waitFor();
		} finally {
			tempScript.delete();
		}
