    import org.apache.beam.sdk.options.PipelineOptionsFactory;
    
    import org.apache.beam.sdk.transforms.*;
    
    import org.apache.beam.sdk.values.PCollection;
    
    public class ReadingFile {
    
        private static class PrintElementFn extends DoFn&lt;String, Void&gt; {
    
            @ProcessElement
            public void processElement(@Element String input) {
                System.out.println(input);
            }
    
        }
    
        public static void main(String[] args) {
            PipelineOptions options = PipelineOptionsFactory
                    .fromArgs(args)
                    .create();
            Pipeline p = Pipeline.create(options);
    
            PCollection&lt;String&gt; lines = p
                    .apply(
                            &quot;ReadMyFile&quot;,
                            TextIO.read().from(&quot;src/main/resources/king-lear_TXT_FolgerShakespeare.txt&quot;)
                    );
    
    
            PCollection&lt;Long&gt; counter = lines.apply(Count.&lt;String&gt;globally());
            counter.apply(MapElements.via(new SimpleFunction&lt;Long, Long&gt;() {
                @Override
                public Long apply(Long input) {
                    System.out.println(&quot;Count is: &quot; + input);
                    return input;
                }
            }));
    
            p.run().waitUntilFinish();
        }
    }

