    &lt;generatorName&gt;java&lt;/generatorName&gt;
    &lt;library&gt;webclient&lt;/library&gt;
    &lt;typeMappings&gt;string+binary=Resource&lt;/typeMappings&gt;
    &lt;importMappings&gt;Resource=org.springframework.core.io.Resource&lt;/importMappings&gt;

