import com.microsoft.aad.msal4j.ClientCredentialFactory;
import com.microsoft.aad.msal4j.ClientCredentialParameters;
import com.microsoft.aad.msal4j.ConfidentialClientApplication;
import com.microsoft.aad.msal4j.IAuthenticationResult;
import com.microsoft.aad.msal4j.IClientCredential;
import com.pointclickcare.sop.iamauthn.config.KafkaProperties;
import lombok.extern.slf4j.Slf4j;
import org.apache.kafka.clients.producer.ProducerConfig;
import org.apache.kafka.common.errors.TimeoutException;
import org.apache.kafka.common.security.auth.AuthenticateCallbackHandler;
import org.apache.kafka.common.security.oauthbearer.OAuthBearerToken;
import org.apache.kafka.common.security.oauthbearer.OAuthBearerTokenCallback;

import java.io.IOException;
import java.net.MalformedURLException;
import java.net.URI;
import java.util.Collections;
import java.util.List;
import java.util.Map;
import java.util.concurrent.ExecutionException;
import javax.security.auth.callback.Callback;
import javax.security.auth.callback.UnsupportedCallbackException;
import javax.security.auth.login.AppConfigurationEntry;

@Slf4j
public class CustomAuthenticateCallbackHandler implements AuthenticateCallbackHandler {

  private static final String HTTPS = &quot;https&quot;;
  private static final String URL_PROTOCOL_DELIMITER = &quot;://&quot;;
  private static final String DEFAULT_ENDPOINT = &quot;/.default&quot;;
  private ConfidentialClientApplication aadClient;
  private ClientCredentialParameters aadParameters;
  private String appId;
  private String appSecret;
  private String authUrl;

  @Override
  public void configure(Map&lt;String, ?&gt; configs, String mechanism,
                        List&lt;AppConfigurationEntry&gt; jaasConfigEntries) {

    String bootstrapServer =
        (String) ((List) configs.get(ProducerConfig.BOOTSTRAP_SERVERS_CONFIG)).get(0);
    URI uri = URI.create(HTTPS + URL_PROTOCOL_DELIMITER + bootstrapServer);

    String sbUri = uri.getScheme() + URL_PROTOCOL_DELIMITER + uri.getHost();

    // based on Azure documentation: The scope to request for a client credential flow is the name
    // of the resource followed by /.default
    this.aadParameters =
        ClientCredentialParameters.builder(Collections.singleton(sbUri + DEFAULT_ENDPOINT)).build();

    Map&lt;String, ?&gt; options = jaasConfigEntries.get(0).getOptions();
    this.appId = (String) options.get(KafkaProperties.CUSTOM_CONFIG_APP_ID);
    this.appSecret = (String) options
        .get(KafkaProperties.CUSTOM_CONFIG_APP_SECRET);
    this.authUrl = (String) options
        .get(KafkaProperties.CUSTOM_CONFIG_AUTHORITY_URL);
  }

  /**
   * Empty method not implementing it, since it is not implemented in other library implementation
   * as well.
   */
  @Override
  public void close() {
    // Do nothing
  }

  @Override
  public void handle(Callback[] callbacks) throws IOException, UnsupportedCallbackException {
    for (Callback callback : callbacks) {
      if (callback instanceof OAuthBearerTokenCallback) {
        try {
          // get token from Azure
          OAuthBearerToken token = getOAuthBearerToken();
          OAuthBearerTokenCallback oauthCallback = (OAuthBearerTokenCallback) callback;
          // let kafka oauth callback to handle the token
          oauthCallback.token(token);
        } catch (InterruptedException | ExecutionException | TimeoutException exp) {
          log.error(&quot;error when get oauth bearer token during the &quot;
              + &quot;eventhub oauth authentication&quot;, exp);
          // Restore interrupted state...
          Thread.currentThread().interrupt();
          throw new IOException(
              &quot;error when get oauth bearer token in custom authenticate callback handler&quot;, exp);
        }
      } else {
        throw new UnsupportedCallbackException(callback);
      }
    }
  }

  OAuthBearerToken getOAuthBearerToken()
      throws MalformedURLException, InterruptedException, ExecutionException, TimeoutException {
    if (this.aadClient == null) {
      IClientCredential credential = ClientCredentialFactory.createFromSecret(appSecret);
      this.aadClient =
          ConfidentialClientApplication.builder(appId, credential).authority(authUrl).build();
    }
    IAuthenticationResult authResult = this.aadClient.acquireToken(this.aadParameters).get();
    log.info(&quot;Token acquired while authentication eventhub using SPN&quot;);

    return new OAuthBearerTokenImpl(authResult.accessToken(), authResult.expiresOnDate());
  }
}
