    &lt;task:annotation-driven executor=&quot;defaultExecutor&quot;/&gt;
	
	&lt;task:executor id=&quot;defaultExecutor&quot; pool-size=&quot;1&quot; queue-capacity=&quot;0&quot;/&gt;
