                                     , private val overlapLimit: Int) : RecyclerView.Adapter&lt;OverlapRecyclerViewAdapter.CustomViewHolder&gt;() {
    
        private val TAG = OverlapRecyclerViewAdapter::class.java.simpleName
    
        //----array list to be shown
