                handle -&gt; handle.createQuery(&quot;SELECT * from orders WHERE user_id IN (&lt;user_id&gt;)&quot;)
                        .bindList(&quot;user_id&quot;, userIds)
                        .mapTo(Order.class)
                        .collect(toImmutableListMultimap(Order::getUserId, Functions.identity())));
