        for (Map.Entry&lt;String, String&gt; entry : map.entrySet()) {
            String fileName = entry.getKey();
            ZipEntry zipFile = new ZipEntry(fileName);
            zout.putNextEntry(zipFile);
            String fileContent = entry.getValue();
