                for (int i = 0; i &lt; NUM_BARS; i++) {
                    values[i] = i * BAR_HEIGHT / NUM_BARS;
                }

                //InsertionSort Button
                insertSort.setText(&quot;Insertion Sort&quot;);
                this.add(insertSort);
                insertSort.addActionListener(new ActionListener() {
