    @Override // java.awt.event.MouseListener
    public void mouseReleased(MouseEvent event) {
        int x = event.getX();
        int column = x / 64;
        int y = event.getY();
        int row = y / 64;
        List&lt;ChessPiece&gt; list;
        if (whiteTurn) {
            list = white;
        }
        else {
            list = black;
        }
