            String keyVal = &quot;id101&quot; ; //args[2];
            System.out.format(&quot;Retrieving item \&quot;%s\&quot; from \&quot;%s\&quot;\n&quot;, keyVal, tableName);
    
            ProfileCredentialsProvider credentialsProvider = ProfileCredentialsProvider.create();
