				&quot;, startDate=&quot; + startDate +
				&#39;}&#39;;
	}

	public Task(Long id, String name, Integer durationDays, Long dependsOn) {
		this.id = id;
		this.name = name;
		this.durationDays = durationDays;
		this.dependsOn = dependsOn;
	}

