    Calendar cSchedStartCal = Calendar.getInstance(TimeZone.getTimeZone(&quot;GMT&quot;));
    //change the timezone
    cSchedStartCal.setTimeZone(TimeZone.getTimeZone(&quot;Asia/Calcutta&quot;));
